import { useContext } from "react";
import { TaskContext } from "../contexts/TaskContext";
import TaskItem from "./TaskItem";

const TaskList = () => {
    const { tasks } = useContext(TaskContext)
    return (
        <ul>
            {tasks.map(x =>
                <TaskItem
                    key={x._id}
                    task={x}
                />
            )}
        </ul>
    );
};

export default TaskList;
